atom-workspace .tree-view {
  color: #9d9e9f;
  font-family: '.SFNSText-Regular', 'SF UI Text', 'Helvetica Neue', 'Arial', 'Lucida Grande', 'Segoe UI', Ubuntu, Cantarell, sans-serif;
  font-weight: 400;
  font-size: 13px;
  background: #282a2b;
  padding: 0;
  box-sizing: border-box;
}
atom-workspace .tree-view > .directory > .header {
  transition: all 400ms ease;
  background: #282a2b;
  height: 32px;
  line-height: 32px;
}
atom-workspace .tree-view > .directory > .header span {
  color: #9d9e9f;
  font-family: '.SFNSText-Bold', 'SF UI Text', 'Helvetica Neue', 'Arial', 'Lucida Grande', 'Segoe UI', Ubuntu, Cantarell, sans-serif;
  font-size: 13px;
  font-weight: 600;
  color: #ffffff;
  transition: all 400ms ease;
}
atom-workspace .tree-view > .directory > .header span.icon:before {
  display: none;
}
atom-workspace .tree-view > .directory > .header:hover span {
  color: #ffffff;
}
atom-workspace .tree-view > .directory > .header:before {
  visibility: hidden;
}
atom-workspace .tree-view > .directory > .header:after {
  content: "hide";
  color: #9d9e9f;
  font-family: '.SFNSText-Light', 'SF UI Text', 'Helvetica Neue', 'Arial', 'Lucida Grande', 'Segoe UI', Ubuntu, Cantarell, sans-serif;
  font-size: 13px;
  font-weight: 200;
  font-size: inherit;
  color: #5d6062;
  font-size: 10px;
  position: absolute;
  right: 10px;
  opacity: 0;
  text-transform: uppercase;
  transform: scale(0);
  transition: all 400ms ease;
}
atom-workspace .tree-view > .directory > .header:hover {
  background: #414346;
}
atom-workspace .tree-view > .directory > .header:hover:after {
  opacity: 1.0;
  transform: scale(1);
}
atom-workspace .tree-view > .directory > .entries {
  opacity: 1;
  padding-bottom: 10px;
  transition: all 400ms ease;
}
atom-workspace .tree-view > .directory.collapsed > .header span {
  color: #5d6062;
}
atom-workspace .tree-view > .directory.collapsed > .header:hover span {
  color: #ffffff;
}
atom-workspace .tree-view > .directory.collapsed > .header:after {
